import 'dart:convert';import 'package:flutter/material.dart';import 'package:http/http.dart' as http;import 'package:yunusco_group/helper_class/dashboard_helpers.dart';import 'package:yunusco_group/service_class/api_services.dart';import 'package:yunusco_group/utils/constants.dart';import '../models/user_model.dart';class AuthProvider with ChangeNotifier {  UserModel? _user;  bool _isLoading = false;  UserModel? get user => _user;  bool get isLoading => _isLoading;  bool get isAuthenticated => _user != null;  // ðŸ“Œ Load User from Shared Preferences  Future<void> loadUser() async {    _user = await DashboardHelpers.getUser();    notifyListeners();  }  // ðŸ“Œ Login API Call  Future<bool> login(String email, String password) async {    _isLoading = true;    notifyListeners();    ApiService apiService=ApiService();    //   "username":"Samin",    //       "password":"Abc@#\$02"    var response=await apiService.postData2('http://192.168.15.6:8085/${AppConstants.loginUrl}',{      "username":email,      "password":password    });    if(response!=null){      _user = UserModel.fromJson(response['returnvalue']['login']);      DashboardHelpers.currentUser=_user;      await DashboardHelpers.saveUser(_user!);      DashboardHelpers.setToken(response['token']??'');      _isLoading = false;      notifyListeners();      return true;    }    else {      _isLoading = false;      notifyListeners();      return false;    }  }  //  //Management Assistant at Vorer Bangla (Online News Paper) 2020  //Danesh Money Exchange (Online transaction) 2023  //  // ðŸ“Œ Logout  Future<void> logout() async {    await DashboardHelpers.clearUser();    _user = null;    AppConstants.token='';    notifyListeners();  }  void setIsLoading(bool val) {    _isLoading=val;    notifyListeners();  }}